  ctx.lineTo((elbow.x + offset.x)*scaler, (elbow.y+offset.y)*scaler);
  ctx.stroke();
  
  ctx.beginPath();
  ctx.moveTo((shoulder.x + offset.x)*scaler, (shoulder.y+ offset.y)*scaler);
